// Theme Toggle Functionality
(function() {
    // Get theme toggle buttons
    var themeToggleBtn = document.getElementById('theme-toggle-btn');
    var themeToggleBtnMobile = document.getElementById('theme-toggle-btn-mobile');
    var themeIcon = document.getElementById('theme-icon');
    var themeIconMobile = document.getElementById('theme-icon-mobile');

    // Check for saved theme preference or default to light mode
    var currentTheme = localStorage.getItem('theme') || 'light';

    // Apply the current theme on load
    if (currentTheme === 'dark') {
        document.body.classList.add('dark-theme');
        if (themeIcon) themeIcon.className = 'fa fa-sun-o';
        if (themeIconMobile) themeIconMobile.className = 'fa fa-sun-o';
    }

    // Function to toggle theme
    function toggleTheme() {
        var isDark = document.body.classList.contains('dark-theme');

        if (isDark) {
            // Switch to light theme
            document.body.classList.remove('dark-theme');
            if (themeIcon) themeIcon.className = 'fa fa-moon-o';
            if (themeIconMobile) themeIconMobile.className = 'fa fa-moon-o';
            localStorage.setItem('theme', 'light');
        } else {
            // Switch to dark theme
            document.body.classList.add('dark-theme');
            if (themeIcon) themeIcon.className = 'fa fa-sun-o';
            if (themeIconMobile) themeIconMobile.className = 'fa fa-sun-o';
            localStorage.setItem('theme', 'dark');
        }
    }

    // Add event listeners
    if (themeToggleBtn) {
        themeToggleBtn.addEventListener('click', toggleTheme);
    }
    if (themeToggleBtnMobile) {
        themeToggleBtnMobile.addEventListener('click', toggleTheme);
    }

})();

// Animated Grid Background - Magic UI Style Implementation
(function() {
    // Configuration - matching Magic UI defaults
    var config = {
        width: 40,
        height: 40,
        x: -1,
        y: -1,
        numSquares: 50,
        maxOpacity: 0.5,
        duration: 4
    };

    var container = document.querySelector('.animated-grid-background');
    var squaresContainer = document.querySelector('.animated-grid-squares');

    if (!container || !squaresContainer) return;

    var dimensions = { width: 0, height: 0 };
    var squares = [];
    var resizeObserver = null;

    // Generate random position
    function getPos() {
        return [
            Math.floor((Math.random() * dimensions.width) / config.width),
            Math.floor((Math.random() * dimensions.height) / config.height)
        ];
    }

    // Generate squares array
    function generateSquares(count) {
        return Array.from({ length: count }, function(_, i) {
            return {
                id: i,
                pos: getPos()
            };
        });
    }

    // Update square position
    function updateSquarePosition(id) {
        var squareIndex = squares.findIndex(function(sq) { return sq.id === id; });
        if (squareIndex !== -1) {
            squares[squareIndex].pos = getPos();
            renderSquare(squares[squareIndex]);
        }
    }

    // Render a single square
    function renderSquare(square) {
        var squareElement = document.getElementById('animated-grid-square-' + square.id);
        if (squareElement) {
            var x = square.pos[0] * config.width + 1;
            var y = square.pos[1] * config.height + 1;
            squareElement.setAttribute('x', x);
            squareElement.setAttribute('y', y);
        }
    }

    // Render all squares
    function renderSquares() {
        squaresContainer.innerHTML = '';

        squares.forEach(function(square, index) {
            var rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
            rect.setAttribute('id', 'animated-grid-square-' + square.id);
            rect.setAttribute('class', 'animated-grid-square');
            rect.setAttribute('width', config.width - 1);
            rect.setAttribute('height', config.height - 1);
            rect.setAttribute('x', square.pos[0] * config.width + 1);
            rect.setAttribute('y', square.pos[1] * config.height + 1);
            rect.setAttribute('fill', 'currentColor');
            rect.setAttribute('stroke-width', '0');

            // Add animation delay based on index
            rect.style.animationDelay = (index * 0.1) + 's';

            // Add animation complete handler
            rect.addEventListener('animationiteration', function() {
                updateSquarePosition(square.id);
            });

            squaresContainer.appendChild(rect);
        });
    }

    // Initialize squares
    function initSquares() {
        if (dimensions.width && dimensions.height) {
            squares = generateSquares(config.numSquares);
            renderSquares();
        }
    }

    // Setup ResizeObserver
    function setupResizeObserver() {
        if (window.ResizeObserver) {
            resizeObserver = new ResizeObserver(function(entries) {
                entries.forEach(function(entry) {
                    dimensions.width = entry.contentRect.width;
                    dimensions.height = entry.contentRect.height;
                    initSquares();
                });
            });

            resizeObserver.observe(container);
        } else {
            // Fallback for browsers without ResizeObserver
            function updateDimensions() {
                dimensions.width = container.offsetWidth;
                dimensions.height = container.offsetHeight;
                initSquares();
            }

            updateDimensions();
            window.addEventListener('resize', updateDimensions);
        }
    }

    // Initialize
    function init() {
        setupResizeObserver();
        initSquares();
    }

    // Start when DOM is ready
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', init);
    } else {
        init();
    }

})();

// Scroll to Top Button Functionality
(function() {
    var scrollToTopBtn = document.getElementById('scroll-to-top');

    if (scrollToTopBtn) {
        // Show/hide button based on scroll position
        window.addEventListener('scroll', function() {
            if (window.pageYOffset > 300) {
                scrollToTopBtn.classList.add('show');
            } else {
                scrollToTopBtn.classList.remove('show');
            }
        });

        // Scroll to top when clicked
        scrollToTopBtn.addEventListener('click', function() {
            window.scrollTo({
                top: 0,
                behavior: 'smooth'
            });
        });
    }
})();

// Telegram Bot Integration
(function() {
    var chatToggle = document.getElementById('live-chat-toggle');
    var chatWidget = document.getElementById('live-chat-widget');
    var chatClose = document.getElementById('live-chat-close');
    var chatInput = document.getElementById('live-chat-input');
    var chatMessages = document.getElementById('live-chat-messages');

    var BOT_TOKEN = '7561789917:AAFo1MemMZ-rcDV026TWA4c1QEik_iTS3MA';
    var CHAT_ID = '1392464241'; // Your Telegram chat ID

    var lastUpdateId = 0;
    var chatHistory = JSON.parse(localStorage.getItem('telegram_chat_history') || '[]');

    if (chatToggle && chatWidget) {
        // Toggle chat widget
        chatToggle.addEventListener('click', function() {
            if (chatWidget.style.display === 'flex') {
                chatWidget.style.display = 'none';
                chatToggle.classList.remove('active');
            } else {
                chatWidget.style.display = 'flex';
                chatToggle.classList.add('active');
                // Focus on input when opened
                if (chatInput) chatInput.focus();
                // Start polling for new messages when chat is opened
                startPolling();
            }
        });

        // Close chat widget
        if (chatClose) {
            chatClose.addEventListener('click', function() {
                chatWidget.style.display = 'none';
                chatToggle.classList.remove('active');
                stopPolling();
            });
        }

        // Handle chat input - Send to Telegram Bot
        if (chatInput) {
            chatInput.addEventListener('keypress', function(e) {
                if (e.key === 'Enter' && this.value.trim() !== '') {
                    var userMessage = this.value.trim();
                    this.value = '';

                    // Add user message to chat
                    addMessage(userMessage, 'user-message');

                    // Send message to Telegram bot
                    sendToTelegram(userMessage);
                }
            });
        }

        // Function to send message to Telegram bot
        function sendToTelegram(message) {
            var url = 'https://api.telegram.org/bot' + BOT_TOKEN + '/sendMessage';
            var data = {
                chat_id: CHAT_ID,
                text: 'New message from website:\n\n' + message,
                parse_mode: 'HTML'
            };

            fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(data)
            })
            .then(response => response.json())
            .then(data => {
                if (!data.ok) {
                    console.error('Telegram API Error:', data);
                    addMessage('Failed to send message. Please try again.', 'bot-message error');
                }
            })
            .catch(error => {
                console.error('Error sending message to Telegram:', error);
                addMessage('Failed to send message. Please check your connection.', 'bot-message error');
            });
        }

        // Function to poll for new messages from Telegram
        var pollingInterval;
        function startPolling() {
            if (pollingInterval) return; // Already polling

            pollingInterval = setInterval(function() {
                checkForNewMessages();
            }, 5000); // Check every 5 seconds
        }

        function stopPolling() {
            if (pollingInterval) {
                clearInterval(pollingInterval);
                pollingInterval = null;
            }
        }

        function checkForNewMessages() {
            var url = 'https://api.telegram.org/bot' + BOT_TOKEN + '/getUpdates';
            if (lastUpdateId > 0) {
                url += '?offset=' + (lastUpdateId + 1);
            }

            fetch(url)
            .then(response => response.json())
            .then(data => {
                if (data.ok && data.result && data.result.length > 0) {
                    data.result.forEach(function(update) {
                        console.log('Received update:', update); // Debug log

                        // Check if this is a message from the admin (not from bot)
                        if (update.message &&
                            update.message.from &&
                            update.message.from.is_bot !== true &&
                            update.message.text &&
                            !isMessageAlreadyDisplayed(update.update_id)) {

                            // This is a new message from the admin
                            var adminMessage = update.message.text;
                            console.log('Adding admin message:', adminMessage); // Debug log
                            addMessage(adminMessage, 'admin-message');

                            // Mark as displayed
                            markMessageAsDisplayed(update.update_id);
                        }

                        if (update.update_id > lastUpdateId) {
                            lastUpdateId = update.update_id;
                        }
                    });
                } else {
                    console.log('No new updates or error:', data); // Debug log
                }
            })
            .catch(error => {
                console.error('Error checking for new messages:', error);
            });
        }

        function isMessageAlreadyDisplayed(updateId) {
            var displayedMessages = JSON.parse(localStorage.getItem('displayed_messages') || '[]');
            return displayedMessages.includes(updateId);
        }

        function markMessageAsDisplayed(updateId) {
            var displayedMessages = JSON.parse(localStorage.getItem('displayed_messages') || '[]');
            displayedMessages.push(updateId);
            localStorage.setItem('displayed_messages', JSON.stringify(displayedMessages));
        }

        // Function to add messages to chat
        function addMessage(text, className) {
            if (chatMessages) {
                var messageDiv = document.createElement('div');
                messageDiv.className = 'message ' + className;
                messageDiv.innerHTML = '<p>' + text + '</p>';

                // Add timestamp
                var timestamp = new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
                messageDiv.innerHTML += '<span class="message-time">' + timestamp + '</span>';

                chatMessages.appendChild(messageDiv);
                chatMessages.scrollTop = chatMessages.scrollHeight;

                // Save to chat history
                saveMessageToHistory(text, className, timestamp);
            }
        }

        // Function to save message to history
        function saveMessageToHistory(text, className, timestamp) {
            chatHistory.push({
                text: text,
                className: className,
                timestamp: timestamp
            });

            // Keep only last 50 messages
            if (chatHistory.length > 50) {
                chatHistory = chatHistory.slice(-50);
            }

            localStorage.setItem('telegram_chat_history', JSON.stringify(chatHistory));
        }

        // Function to load chat history
        function loadChatHistory() {
            chatHistory.forEach(function(msg) {
                if (chatMessages) {
                    var messageDiv = document.createElement('div');
                    messageDiv.className = 'message ' + msg.className;
                    messageDiv.innerHTML = '<p>' + msg.text + '</p><span class="message-time">' + msg.timestamp + '</span>';
                    chatMessages.appendChild(messageDiv);
                }
            });

            if (chatMessages && chatHistory.length > 0) {
                chatMessages.scrollTop = chatMessages.scrollHeight;
            }
        }

        // Load chat history on page load
        loadChatHistory();
    }
})();
//
